const hiddenCode = "XXXXXXXXXXXXXXXXXXXXXXXXXXXCXXXXXXXOXXXXXDXXXXXXXXXXXXXXXEXXXX XXXXXXXXXXXXXXX:";

let decodedCode = "";

const hiddenCodeArray = hiddenCode.split("");


for (let i = 0; i < hiddenCodeArray.length; i++) {
    const char = hiddenCodeArray[i];
    
    if (char !== "X" && (Number.isNaN(Number(char)) || char % 2 !== 0)) {
        decodedCode += char;
    }
}


console.log(decodedCode);
